    fun addDataToDb(realm: Realm){
        var count = 0
        dataset.keys.forEach { k ->
            val c = dataset[k]
            if (c!=null) {
                val cd = ClassifierData().apply {
                    id = ++count + 0L
                    text = k
                    category = c
                }
                realm.insertOrUpdate(cd)
            }
        }
        Log.e("ClassifierDataSet", "Data Added")
    }

